import React, { useCallback, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import { getEmps } from "../actions";
import "./HomePage.css"

function HomePage() {
  const user = useSelector((state) => state.authentication.user);
  const menu = useSelector((state) => state.empList.items);
  const dispatch = useDispatch();


  const getEmpl = useCallback(async () => {
    await dispatch(getEmps(user.UserId));
  }, [dispatch]);


// const getMessage = useCallback(() => {
//   `${user && user.name}`,
//   [user, menu]
//   console.log("get",getMessage)
// })

  useEffect(() => {
    getEmpl();
  }, []);

  return (
    <div>
      <h1>Hi {user.user}!</h1>
      <h3>Employee List</h3>
      <p className="floatrt">
        <Link to="/login">Logout</Link>
      </p>
      <ul>
     {menu.map(item => (
     <li key={item.entity_cd}>
       <h2>{item.entity_cd}</h2>
       <p>{item.entity_cd}</p>
       </li>
     ))}
     </ul>

    </div>
  );
}

export { HomePage };
