//   [user, menu]
//   console.log("get",getMessage)
// })


  return (
    <div>
      <h1>Hi {user.user}!</h1>
      <h3>Employee List</h3>
      <p className="floatrt">
        <Link to="/login">Logout</Link>
      </p>
      <ul>
      {post.map((name, index) => (
    <li key={index}>
      {name}
    </li>
  ))}
     </ul>

    </div>
  );
}

export { HomePage };
