import { login } from "../data/userControl";
import EmpData from "../data/DashBoardPage.json";
import httpClient from "../data/httpClient"


class userService {
  constructor() {
    this.appPath = "/c_approval"
    this.basePath = "/c_auth";

  }
}

 loginUser = async (email, password) => {
  try {
    const result = await httpClient.request({
      url: `${this.basePath}/login`,
      method: "POST",
      data: {
        email,
        password,
        token: "",
        token_firebase: "token test",
        device: "ios",
        mac: "mac test",
        app: "A"
      }
    });
    if (result.Error) {
      return Promise.reject(result.Pesan);
    } else {
      return result;
    }
  } catch (error) {
    return Promise.reject(error);
  }
};

 export const getEmp = async (userId) => {
  try {
    const result = httpClient.request({
      url: `c_approval/groupMenu`,
      method: "POST",
      data: {
        approval_user: userId,

      },
    });
    return  result;
  } catch (error) {
    return Promise.reject(error);
  }
}
