import React, { useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  Flex,
  Box,
  Heading,
  FormControl,
  FormLabel,
  Input,
  Button,
  CircularProgress,
  Text,
  InputGroup,
  InputRightElement,
  Icon,
  Divider,
  SimpleGrid,
  Grid
} from '@chakra-ui/core';

import { getEmps } from "../actions";
import "./HomePage.css"

function HomePage() {
  const users = useSelector((state) => state.empList);
  const user = useSelector((state) => state.authentication.user);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getEmps(user.UserId));
  }, []);

  return (
    <>
     <div align="center" justifyContent="center">
      <h1>Hi {user.name}!</h1>
      <p  float="right">
        <Link to="/login">Logout</Link>
      </p>
      </div>
      <Divider />
      <Flex align="center" justifyContent="center">
          {users.items && users.items.map((item, idx) => {
            return (

              <Box
              p={8}
              display="grid"
              column={2}
              m={8}
              maxWidth="500px"
              borderWidth={1}
              borderRadius={8}
              boxShadow="lg"
              flexWrap="wrap"
              key={idx}>
                <Text>{item.entity_cd}</Text>
                <Text>{item.entity_name}</Text>
                <Divider/>
            <h1>Total : {item.total}</h1>
              </Box>


            )
          })
        }
         <div className="table-responsive">
        {users.items && users.items.length > 0 && (
          <table className="table">
            <thead className="thead-dark">
              <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Age</th>
                <th>Gender</th>
                <th>Email</th>
                <th>PhoneNo</th>
              </tr>
            </thead>
            <tbody>
              {users.items &&
                users.items.map((user, index) => (
                  <tr keys={user.id}>
                    <th>{user.id}</th>
                    <th>{user.name}</th>
                    <th>{user.age}</th>
                    <th>{user.gender}</th>
                    <th>{user.email}</th>
                    <th>{user.phoneNo}</th>
                  </tr>
                ))}
            </tbody>
          </table>
        )}
        </div>
      </Flex>
    </>
  );
}

export { HomePage };
