import { login } from "../data/userControl";
import EmpData from "../data/DashBoardPage.json";
import httpClient from "../data/httpClient"


export const menuService = {
  getMenu,
  getDetail,
  getViewDetail
};



function getMenu(userId, entity_cd) {
  try {
    const result = httpClient.request({
      url: `c_approval/approvalMenu`,
      method: "POST",
      data: {
        approval_user : userId,
        entity_cd : entity_cd

      },
    });
    return result;
  } catch (error) {
    return Promise.reject(error);
  }
}


function getDetail(approval_user, approval_id, entity_cd) {
  try {
    const result = httpClient.request({
      url: `c_approval/viewMenu`,
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      data: {
          approval_user: approval_user,
          approval_id: approval_id,
          entity_cd: entity_cd,
      },
    });
    return result;
  } catch (error) {
    return Promise.reject(error);
  }
}


function getViewDetail(entity, docNo, approval_user, approval_id) {
  try {
    const result = httpClient.request({
      url: `c_approval/otorisasi`,
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      data: {
        entity_cd: entity,
        request_no: docNo,
        approval_user: approval_user,
        approval_id: approval_id,
      },
    });
    return result;
  } catch (error) {
    return Promise.reject(error);
  }
}
