import React, { useCallback, useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  Flex,
  Box,
  Heading,
  FormControl,
  FormLabel,
  Input,
  Button,
  CircularProgress,
  Text,
  InputGroup,
  InputRightElement,
  Icon,
  Divider,
  SimpleGrid,
  Grid,
  Avatar
} from '@chakra-ui/core';

import { getApproval } from "../actions";

function ApprovalPage() {
  const menu = useSelector((state) => state.menuApprove.items);
  const users = useSelector((state) => state.empList);
  const user = useSelector((state) => state.authentication.user);
  const dispatch = useDispatch();


  useEffect(() => {
    dispatch(getApproval(menu));
  }, []);

  return (
    <>
     <div align="center" justifyContent="center">
       <Avatar src={user.pict} m="4"/>
      <h1>Hi {user.name}!</h1>
      <p  float="right">
        <Link to="/login">Logout</Link>
      </p>
      </div>
      <div align="center" justifyContent="center">

      <Flex flexWrap="wrap" align="center" justifyContent="center" maxW="700px" mt="10">
          {menu.items && menu.items.map((item, idx) => {
            return (

              <Box
              p="6" m="4"
              align="center"
              justifyContent="center"
              borderWidth="1px"
              rounded="lg" 
              flexBasis="45%"
              variantColor="orange"
              variant="outline"
              maxWidth="500px"
              borderRadius={8}
              boxShadow="lg"
              key={idx}>
                <Text fontSize="xl" color="#F6AD55"
>{item.entity_cd}</Text>
                <Divider/>
                {/* <Text>{item.entity_name}</Text>
                <Divider/>
            <Button fontSize="xl"  p="4">Total : <Text color="#38B2AC" m="2">{item.total}</Text></Button> */}
              </Box>


            )
          })
        } 

      </Flex>
      </div>
    </>
  );
}

export { ApprovalPage };
