import React, { useCallback, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  Flex,
  Box,
  Heading,
  FormControl,
  FormLabel,
  Input,
  Button,
  CircularProgress,
  Text,
  InputGroup,
  InputRightElement,
  Icon,
  Divider,
  SimpleGrid,
  Grid,
  Avatar
} from '@chakra-ui/core';

import { getViewDetail } from "../actions";


const status_descs = {
  P: "Pending",
  R: "Revise",
  C: "Cancel",
  A: "Approve"
};

function ViewDetail(props) {
    console.log("peorss",props)

    const {
        request_dept_name,
    request_staff_id,
    doc_no,
    request_no,
    amount,
    entity_cd,
    entity_name,
    descs,
    doc_date,
    module: modules,
    document_descs,
    approval_id,
    approval_user,
        }= props.location.state.Data;
    console.log("viewdetail", props.location.state.Data)

    const data = props.location.state.Data

  const viewdetail = useSelector((state) => state.viewdetail);

  const details = useSelector((state) => state.detail);
  const user = useSelector((state) => state.authentication.user);
  const [activeType, setActiveType] = useState("");
  const [remarks, setRemarks] = useState("");


  const dispatch = useDispatch();
  const handleRemarks = val => {
    setRemarks(val);
    data.reason_remarks = val;
  };

  const onPressed = type => {
    setModalVisible(true);
    if (type == "A") {
      setRemarks("");
    }
    setActiveType(type);
  };


  useEffect(() => {
    dispatch(getViewDetail(
        user.UserId,
        entity_cd, doc_no, approval_id, approval_user
        ));
  }, []);


  return (
    <>
     <div align="center" justifyContent="center">
     <Avatar src={user.pict} m="4" />
        <h1>Hi {user.name}!</h1>
      <p  float="right">
        <Link to="/login">Logout</Link>
      </p>
      </div>

      <div align="center" justifyContent="center">
        <Flex
          flexWrap="wrap"
          align="center"
          justifyContent="center"
          maxW="700px"
          mt="10"
        >

          {viewdetail.items &&
            viewdetail.items.map((item, idx) => {
              return (
                <Box
                  p="6"
                  m="4"            
                  borderWidth="1px"
                  rounded="lg"
                  variantColor="orange"
                  variant="outline"
                  maxWidth="500px"
                  borderRadius={8}
                  boxShadow="lg"
                  key={idx}
                >
                    <Flex align="flex-end">
                  <Text fontSize="xl" color="#F6AD55" align="flex-end" >
                    {item.entity_cd}
                  </Text>
                  </Flex>
                  <Text>{item.request_staff_id}</Text>
                  <Divider />
                  <Text>{item.approval_name}</Text>
                  <Text>{item.approval_user}</Text>

                  <Divider />
                  {/* <Button fontSize="xl" p="4">
                    Total :{" "}

                      <Text color="#38B2AC" m="2">
                        {item.total_doc}
                      </Text>
                  </Button> */}
                </Box>
              );
            })}
        </Flex>
      </div>
    </>
  );
}

export { ViewDetail };
